import React, { useEffect, useState } from "react";
import axios from "axios";
import SearchForm from "./Components/SearchForm";
import GifList from "./Components/GifList";

function App() {
  const [gifs, setGifs] = useState([]);
  useEffect(() => {
    axios.get("https://api.giphy.com/v1/gifs/trending?api_key=gm1dfkC7reinIgvyjRaxYMLm2IwjvFjw&limit=24&rating=g")
      .then(response => {setGifs(response.data.data)})
      .catch(error => {console.log("Error fetching and parsing data", error)})

    // FETCHING WITHOUT AXIOS:
    // fetch("https://api.giphy.com/v1/gifs/trending?api_key=gm1dfkC7reinIgvyjRaxYMLm2IwjvFjw&limit=24&rating=g")
    //   .then(response => response.json())
    //   .then(responseData => setGifs(responseData.data))
    //   .catch(error => console.log("Error fetching and parsing data", error));
  }, []);

  return (
    <div>
      <div className="main-header">
        <div className="inner">
          <h1 className="main-title">GifSearch</h1>
          <SearchForm />
        </div>
      </div>
      <div className="main-content">
        <GifList />
      </div>
    </div>
  );
}

export default App;
